package CH01;

public class C02_진수 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//	10진수
		System.out.printf("10진수 : %d\n",173); // 10진수
		System.out.printf("10진수 : %d\n",0255); // o - 8진수
		System.out.printf("10진수 : %d\n",0xAD); // ox - 16진수 
		System.out.printf("10진수 : %d\n",0b10101101); // 0b - 2진수
		
		//	8진수
		System.out.printf("8진수 : %o\n",173); // 10진수
		System.out.printf("8진수 : %o\n",0255); // o - 8진수
		System.out.printf("8진수 : %o\n",0xAD); // ox - 16진수 
		System.out.printf("8진수 : %o\n",0b10101101); // 0b - 2진수
		
		//	16진수
		System.out.printf("16진수 : %x\n",173); // 10진수
		System.out.printf("16진수 : %x\n",0255); // o - 8진수
		System.out.printf("16진수 : %X\n",0xAD); // ox - 16진수 
		System.out.printf("16진수 : %X\n",0b10101101); // 0b - 2진수
	}

}
